mapper.update:
update tbl_member
set member_password=#{memberPassword}
where member_id=#{memberId}

mapper.select:
select member_id, member_identification, member_name, member_phone, member_email, member_type, member_point
from tbl_member
where member_id = #{memberId}

mapper.update:
update tbl_member
set member_name=#{memberName}, member_phone=#{memberPhone}, member_email=#{memberEmail}
where member_id=#{memberId};

update tbl_member
set member_password=#{memberPassword}
where member_email=#{memberEmail}

mapper.select:
select member_random_key
from tbl_member
where member_email=#{memberEmail}

mapper.update:
update tbl_member
set member_random_key = #{memberRandomKey}
where member_email = #{memberEmail}
