mapper.select:
select member_id, member_identification, member_name, member_phone, member_email, member_type
from tbl_member
where member_id = #{memberId}

mapper.update:
update tbl_member
set member_identification=#{memberIdentification}, member_password='', member_name='', member_phone='', member_email='', member_file_system_name='', member_type='', member_drive_register_date='', member_point=0
where member_id=#{memberId};
